SELECT MEDIAN(`x`) AS `median`
FROM `df`

lazy_frame(x = c(1, 2), con = con) |&gt;
  mutate(median = median(x, na.rm = TRUE)) |&gt;
  show_query()

&lt;SQL&gt;
SELECT `df`.*, MEDIAN(`x`) OVER () AS `median`
FROM `df`





